mapper.update:
UPDATE CARNAME
   SET CLASS_CODE = #{classCode},
	   CAR_PRICE = #{carPrice}
 WHERE CAR_NAME = #{carName}

UPDATE MANAGEMENT
   SET INVEN_CODE = #{invenCode}
 WHERE CAR_NAME = #{carName}

mapper.select:
SELECT COUNT(*)
  FROM INVENTORY
 WHERE ITEM_CODE = #{itemCode}

SELECT COUNT(*)
  FROM EX_CAR
 WHERE DEALER_PHONE = #{dealerPhone}
